j = join(
	tables: {t1: src, t2: co2eqkwh},
    on: ["cc"],
	)
    |> map(fn: (r) => ({
    r with
    	//system_power_input: r.in_power,
    	//carbonIntensity: r._value_t2,
    	//region: r.region_t1,
    	//country: r.country_t1,
      _value: float(v:r._value_t1) * float(v:r._value_t2) / 1000.0,
    	_time: r._time_t1
	}))
    //|> keep(columns: ["_value", "_time", "cc", "source", "subscription", "_measurement", "region_t1", "country_t1"])
    |> keep(columns: ["_value", "_time", "cc", "source", "subscription", "_measurement"])

